var BkDutyLevel = (global.get("bk_manual_duty"));
var BkDutyLevelStep = 1;

var loopLimit = 4;
var repeating  = context.get("repeating") || 0;

var out = null;
var outLoop = null;

function setBkDutyLevel(dir) {
    if(dir > 0) {
        BkDutyLevel += BkDutyLevelStep;
    } 
    else {
        BkDutyLevel -= BkDutyLevelStep;
    }
    if(BkDutyLevel <= 1) {BkDutyLevel = 1;}
    if(BkDutyLevel >=100) {BkDutyLevel = 100;}
    out = {"payload": (BkDutyLevel)};
}

if (msg.topic == "pi/29" && msg.payload == "1"  || (msg.payload == "BkDutyLevelIncrease" && repeating) ) {
    setBkDutyLevel(+1);
    outLoop = {"payload": "BkDutyLevelIncrease"};
    repeating++;
}
else if (msg.topic == "pi/31" && msg.payload == "1"  || (msg.payload == "BkDutyLevelDecrease" && repeating) ) {
    setBkDutyLevel(-1);
    outLoop = {"payload": "BkDutyLevelDecrease"};
    repeating++;
}
else if (msg.payload == "0" || repeating > loopLimit) {
    repeating = 0;
}

context.set("repeating", repeating);

return [out, outLoop];

